package Advent_Of_Code.AdventOfCode2022;

public class Dec6 {
    public static void main(String[] args) {
        String x = "";
        String y = "";
        //part 1: 
        //int len = 4;
        //part 2: 
        int len = 14; 
        boolean z = true;
        for (int i = 0; i < x.length() - len; i++) {
            y = x.substring(i, i + len); 
            for (int j = 0; j < y.length(); j++) {
                if (y.substring(j + 1).contains(y.charAt(j) + ""))
                    z = false;
            }
            if (z) {
                System.out.println(i + len + " " + y);
                break;
            }
            z = true;
        }
    }
}
